import React, { useState, useEffect, useContext, useCallback } from "react";
import { useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
// Assuming an update function exists in your groupService
import {
  getGroupDetails,
  updateGroupBudget,
  addBudgetPlan,
  getGroupActivity,
} from "../api/groupService";
import { getGroupExpenses } from "../api/expenseService";
import {
  getGroupBalances,
  getSettlementHistory,
} from "../api/settlementService"; // Import the balance service
import AddExpenseForm from "../components/AddExpenseForm";
import ExpenseList from "../components/ExpenseList";
import AddMemberForm from "../components/AddMemberForm";
import GroupBalances from "../components/GroupBalances";
import ActivityFeed from "../components/ActivityFeed"; // Import the new component
import SettleUpForm from "../components/SettleUpForm";

const GroupDetail = () => {
  // --- State Management ---
  const [group, setGroup] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [balances, setBalances] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [settlementHistory, setSettlementHistory] = useState([]);
  const [activity, setActivity] = useState([]); // State for combined activity

  // New state for managing budget editing
  const [isEditingBudget, setIsEditingBudget] = useState(false);
  const [newBudget, setNewBudget] = useState("");
  // --- New state for adding a category budget plan ---
  const [showAddPlanForm, setShowAddPlanForm] = useState(false);
  const [newCategory, setNewCategory] = useState("");
  const [newLimit, setNewLimit] = useState("");
  // --- Hooks and Context ---
  const { id: groupId } = useParams(); // Use 'groupId' consistently
  const { user: currentUser } = useContext(AuthContext); // Use 'currentUser' consistently
  const [addPlanError, setAddPlanError] = useState("");

  // --- Data Fetching ---
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  useEffect(() => {
    const fetchAllGroupData = async () => {
      if (!currentUser?.token || !groupId) {
        setLoading(false);
        return;
      }

      setLoading(true);
      try {
        const [
          groupData,
          expensesData,
          balancesData,
          historyData,
          activityData,
        ] = await Promise.all([
          getGroupDetails(groupId, currentUser.token),
          getGroupExpenses(groupId, currentUser.token),
          getGroupBalances(groupId, currentUser.token),
          getSettlementHistory(groupId, currentUser.token),
          getGroupActivity(groupId, currentUser.token),
        ]);

        setGroup(groupData);
        setNewBudget(groupData.budget || "");
        setExpenses(expensesData);
        setBalances(balancesData);
        setSettlementHistory(historyData || []);
        setGroup(groupData);
        setActivity(activityData);
        setError("");
      } catch (err) {
        setError("Failed to load group data. You may not be a member.");
      } finally {
        setLoading(false);
      }
    };

    fetchAllGroupData();
  }, [groupId, currentUser, refreshTrigger]);
  // --- Handlers ---
  const handleDataRefresh = () => {
    setRefreshTrigger((count) => count + 1);
  };

  const fetchOnlyBalances = useCallback(async () => {
    if (!currentUser?.token || !groupId) return;
    try {
      const balancesData = await getGroupBalances(groupId, currentUser.token);
      setBalances(balancesData);
    } catch (err) {
      console.error("Failed to refetch balances:", err);
    }
  }, [groupId, currentUser]);

  // This handler now accepts the new settlement object for an optimistic update
  const handleSettlementSuccess = (newSettlementResponse) => {
    // Add the new settlement to the history list instantly
    // We assume the API returns an object with a 'settlement' property
    if (newSettlementResponse && newSettlementResponse.settlement) {
      // THE FIX: Explicitly check if prevHistory is an array before spreading it.
      setSettlementHistory((prevHistory) => {
        const currentHistory = Array.isArray(prevHistory) ? prevHistory : [];
        return [newSettlementResponse.settlement, ...currentHistory];
      });
    }
    // Only refetch the balances, which is more efficient
    fetchOnlyBalances();
  };

  const handleUpdateBudget = async () => {
    if (isNaN(parseFloat(newBudget))) {
      setError("Please enter a valid number for the budget.");
      return;
    }
    try {
      await updateGroupBudget(
        groupId,
        { budget: parseFloat(newBudget) },
        currentUser.token
      );
      setIsEditingBudget(false);
      handleDataRefresh();
    } catch (err) {
      setError("Failed to update budget.");
    }
  };

  // --- New handler for adding a category budget plan ---
  const handleAddBudgetPlan = async (e) => {
    e.preventDefault();
    if (!newCategory.trim() || !newLimit.trim()) {
      setAddPlanError("⚠️ Please enter both a category and a limit.");
      return;
    }
    try {
      await addBudgetPlan(
        groupId,
        {
          category: newCategory.trim(),
          limit: parseFloat(newLimit),
        },
        currentUser.token
      );
      setShowAddPlanForm(false);
      setNewCategory("");
      setNewLimit("");
      setAddPlanError("");
      handleDataRefresh();
    } catch (err) {
      setAddPlanError("❌ Failed to add budget plan.");
    }
  };
  // --- Derived State and Render Logic ---
  if (loading) return <div className="p-8 text-center">Loading...</div>;
  if (error) return <div className="p-8 text-center text-red-600">{error}</div>;
  if (!group) return <div className="p-8 text-center">Group not found.</div>;

  const totalOutstanding = balances
    ? Object.values(balances)
        .filter((data) => data.net > 0)
        .reduce((sum, creditor) => sum + creditor.net, 0)
    : 0;

  const totalSpent = expenses.reduce((sum, e) => sum + e.amount, 0);

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-2">{group.name}</h2>
      <p className="text-gray-500 mb-8"> {group.description}</p>

      {totalOutstanding > 0 && (
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg text-center">
          <p className="text-lg font-semibold text-blue-800">
            Total Outstanding Balance to be Settled:
          </p>
          <p className="text-3xl font-bold text-blue-900">
            ${totalOutstanding.toFixed(2)}
          </p>
        </div>
      )}

      {/* --- Budget Overview and Editing Section --- */}
      <div className="bg-white p-4 rounded-lg shadow mb-6 border">
        <h3 className="text-2xl font-semibold mb-2">Group Budget Overview</h3>
        <div className="flex items-center gap-4">
          {!isEditingBudget ? (
            <div className="flex items-center gap-4">
              <p className="text-gray-700">
                Total Spent: {/* --- THIS IS THE FIX --- */}
                <span
                  className={`font-bold ${
                    group.budget > 0 && totalSpent > group.budget
                      ? "text-red-600" // Over budget
                      : "text-green-600" // Under or at budget
                  }`}
                >
                  ${totalSpent.toFixed(2)}
                </span>
                {group.budget > 0 && (
                  <>
                    {" "}
                    of{" "}
                    <span className="font-bold">
                      ${group.budget.toFixed(2)}
                    </span>
                  </>
                )}
              </p>
              <button
                onClick={() => setIsEditingBudget(true)}
                className="text-sm text-blue-600 hover:underline"
              >
                Edit Budget
              </button>
            </div>
          ) : (
            <div className="flex items-center gap-2">
              <input
                type="number"
                value={newBudget}
                onChange={(e) => setNewBudget(e.target.value)}
                className="p-2 border rounded-md w-32"
                placeholder="Set budget"
              />
              <button
                onClick={handleUpdateBudget}
                className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
              >
                Save
              </button>
              <button
                onClick={() => setIsEditingBudget(false)}
                className="bg-gray-200 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-300"
              >
                Cancel
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="space-y-6">
        <div className="bg-white p-6 rounded-lg shadow-sm border">
          <h3 className="text-2xl font-semibold mb-4">Members</h3>
          <ul className="space-y-2 mb-6">
            {group.members?.map((member) => (
              <li key={member._id} className="p-3 bg-gray-100 rounded-md">
                {member.name}
              </li>
            ))}
          </ul>
          <AddMemberForm groupId={groupId} onMemberAdded={handleDataRefresh} />
        </div>
        <h3 className="text-2xl font-semibold mt-8 mb-4">Group Activity</h3>
        <ActivityFeed items={activity} />

        {/* 
        <GroupBalances
          groupId={groupId}
          balances={balances}
          isLoading={loading}
          onSettlementSuccess={handleDataRefresh}
          settlementHistory={settlementHistory}
        /> */}

        {/* <SettleUpForm
          groupId={groupId}
          members={group.members}
          onDataRefresh={handleDataRefresh}
        /> */}
        <SettleUpForm />
      </div>
      <div className="h-6" />

      {/* --- NEW: Category Budgets Section --- */}
      <div className="bg-white p-6 rounded-lg shadow mb-6 border">
        <h3 className="text-2xl font-semibold mb-4">Category Budgets</h3>
        <div className="space-y-3">
          {group.budgetPlans?.length > 0 ? (
            group.budgetPlans.map((plan) => {
              const spent = expenses
                .filter((e) => e.category === plan.category)
                .reduce((sum, e) => sum + e.amount, 0);
              return (
                <div
                  key={plan.category}
                  className="bg-gray-50 p-3 border rounded-md"
                >
                  <h4 className="font-medium text-gray-800">{plan.category}</h4>
                  <p className="text-sm text-gray-600">
                    ${spent.toFixed(2)} of ${plan.limit.toFixed(2)} used
                  </p>
                  <div className="w-full bg-gray-200 h-2 rounded mt-1">
                    <div
                      className={`h-2 rounded ${
                        spent > plan.limit ? "bg-red-500" : "bg-green-500"
                      }`}
                      style={{
                        width: `${Math.min(100, (spent / plan.limit) * 100)}%`,
                      }}
                    />
                  </div>
                </div>
              );
            })
          ) : (
            <p className="text-gray-500">No category budgets have been set.</p>
          )}
        </div>
        <div className="mt-4">
          {!showAddPlanForm ? (
            <button
              onClick={() => {
                setShowAddPlanForm(true);
                setAddPlanError("");
              }}
              className="text-sm text-blue-600 hover:underline"
            >
              + Add Category Budget
            </button>
          ) : (
            <div className="mt-4 p-4 border-t">
              <form onSubmit={handleAddBudgetPlan}>
                <div className="flex flex-col sm:flex-row sm:items-center gap-2">
                  <input
                    type="text"
                    placeholder="Category Name"
                    value={newCategory}
                    onChange={(e) => setNewCategory(e.target.value)}
                    className="p-2 border rounded-md flex-grow"
                  />
                  <input
                    type="number"
                    placeholder="Limit"
                    value={newLimit}
                    onChange={(e) => setNewLimit(e.target.value)}
                    className="p-2 border rounded-md w-32"
                  />
                  <button
                    type="submit"
                    className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
                  >
                    Save
                  </button>
                  <button
                    type="button"
                    onClick={() => {
                      setShowAddPlanForm(false);
                      setNewCategory("");
                      setNewLimit("");
                      setAddPlanError("");
                    }}
                    className="bg-gray-200 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-300"
                  >
                    Cancel
                  </button>
                </div>
              </form>
              {/* Show only addPlanError here */}
              {addPlanError && (
                <p className="text-red-600 text-sm mt-3 ml-1">{addPlanError}</p>
              )}
            </div>
          )}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        <div className="md:col-span-3">
          <AddExpenseForm
            groupId={groupId}
            members={group.members}
            onExpenseAdded={handleDataRefresh}
            categories={group.budgetPlans?.map((plan) => plan.category)}
          />
          <h3 className="text-2xl font-semibold mt-8 mb-4">Expense History</h3>
          <ExpenseList expenses={expenses} />
        </div>
      </div>
    </div>
  );
};

export default GroupDetail;
