// export default AddExpenseForm;
import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { addExpense } from '../api/expenseService';

// The form now accepts a 'categories' prop
const AddExpenseForm = ({ groupId, members, onExpenseAdded, categories = [] }) => {
    const [description, setDescription] = useState('');
    const [amount, setAmount] = useState('');
    const [category, setCategory] = useState(''); // New state for the category
    const [error, setError] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    
    const { user } = useContext(AuthContext);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!description || !amount || !category) {
            setError('Description, amount, and category are required.');
            return;
        }

        setIsSubmitting(true);
        setError('');

        const expenseData = {
            description,
            amount: parseFloat(amount),
            paidBy: user.userId,
            group: groupId,
            category, // Include the category in the data sent to the API
            // Simple split logic: for now, split equally among all members
            splits: members.map(member => ({
                user: member._id,
                amount: parseFloat(amount) / members.length
            }))
        };

        try {
            await addExpense(expenseData, user.token);
            onExpenseAdded();
            // Reset form fields
            setDescription('');
            setAmount('');
            setCategory('');
        } catch (err) {
            setError(err.message || 'Failed to add expense.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="p-6 bg-white rounded-lg shadow-md border">
            <h3 className="text-xl font-semibold mb-4">Add a New Expense</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
                    <input type="text" id="description" value={description} onChange={(e) => setDescription(e.target.value)} className="mt-1 block w-full p-2 border rounded-md" />
                </div>
                <div>
                    <label htmlFor="amount" className="block text-sm font-medium text-gray-700">Amount</label>
                    <input type="number" id="amount" value={amount} onChange={(e) => setAmount(e.target.value)} className="mt-1 block w-full p-2 border rounded-md" />
                </div>
                
                {/* --- NEW: Category Dropdown --- */}
                <div>
                    <label htmlFor="category" className="block text-sm font-medium text-gray-700">Category</label>
                    <select
                        id="category"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                        className="mt-1 block w-full p-2 border rounded-md bg-white"
                        disabled={!categories || categories.length === 0}
                    >
                        <option value="">{categories && categories.length > 0 ? 'Select a category' : 'No categories defined in budget'}</option>
                        {categories?.map(cat => (
                            <option key={cat} value={cat}>{cat}</option>
                        ))}
                    </select>
                </div>
                
                {error && <p className="text-sm text-red-600">{error}</p>}
                
                <button type="submit" disabled={isSubmitting} className="w-full bg-blue-600 text-white font-bold py-2 px-4 rounded-md hover:bg-blue-700 disabled:bg-blue-300">
                    {isSubmitting ? 'Adding...' : 'Add Expense'}
                </button>
            </form>
        </div>
    );
};

export default AddExpenseForm;
