import React, { useState, useEffect, useContext } from "react";
import { useParams } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
// Assuming an update function exists in your groupService
import { getGroupDetails, updateGroupBudget } from "../api/groupService";
import { getGroupExpenses } from "../api/expenseService";
import { getGroupBalances } from "../api/settlementService"; // Import the balance service
import AddExpenseForm from "../components/AddExpenseForm";
import ExpenseList from "../components/ExpenseList";
import AddMemberForm from "../components/AddMemberForm";
import GroupBalances from "../components/GroupBalances";

const GroupDetail = () => {
  // --- State Management ---
  const [group, setGroup] = useState(null);
  const [expenses, setExpenses] = useState([]);
  const [balances, setBalances] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  // State for managing budget editing
  const [isEditingBudget, setIsEditingBudget] = useState(false);
  const [newBudget, setNewBudget] = useState("");
  // State to toggle the visibility of the AddExpenseForm
  const [showAddExpense, setShowAddExpense] = useState(false);
  // --- NEW: State to toggle the entire expense section ---
  const [showExpenseSection, setShowExpenseSection] = useState(false);
  
  // --- Hooks and Context ---
  const { id: groupId } = useParams(); // Use 'groupId' consistently
  const { user: currentUser } = useContext(AuthContext); // Use 'currentUser' consistently

  // --- Data Fetching ---
  const [refreshTrigger, setRefreshTrigger] = useState(0);

  useEffect(() => {
    const fetchAllGroupData = async () => {
      if (!currentUser?.token || !groupId) {
        setLoading(false);
        return;
      }
      
      setLoading(true);
      try {
        const [groupData, expensesData, balancesData] = await Promise.all([
          getGroupDetails(groupId, currentUser.token),
          getGroupExpenses(groupId, currentUser.token),
          getGroupBalances(groupId, currentUser.token),
        ]);
        
        setGroup(groupData);
        setNewBudget(groupData.budget || ""); // Initialize newBudget state
        setExpenses(expensesData);
        setBalances(balancesData);
        setError("");
      } catch (err) {
        setError("Failed to load group data. You may not be a member.");
      } finally {
        setLoading(false);
      }
    };

    fetchAllGroupData();
  }, [groupId, currentUser, refreshTrigger]);

  // --- Handlers ---
  const handleDataRefresh = () => {
    setRefreshTrigger(count => count + 1);
  };

  const handleUpdateBudget = async () => {
    if (isNaN(parseFloat(newBudget))) {
      setError("Please enter a valid number for the budget.");
      return;
    }
    try {
      await updateGroupBudget(groupId, { budget: parseFloat(newBudget) }, currentUser.token);
      setIsEditingBudget(false);
      handleDataRefresh();
    } catch (err) {
      setError("Failed to update budget.");
    }
  };

  // --- Derived State and Render Logic ---
  if (loading) return <div className="p-8 text-center">Loading...</div>;
  if (error) return <div className="p-8 text-center text-red-600">{error}</div>;
  if (!group) return <div className="p-8 text-center">Group not found.</div>;
  
  const totalOutstanding = balances
    ? Object.values(balances)
        .filter((data) => data.net > 0)
        .reduce((sum, creditor) => sum + creditor.net, 0)
    : 0;
  
  const totalSpent = expenses.reduce((sum, e) => sum + e.amount, 0);

  return (
    <div className="p-8 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-2">{group.name}</h2>
      <p className="text-gray-500 mb-8">Created by: {group.createdBy?.name}</p>

      {totalOutstanding > 0 && (
        <div className="mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg text-center">
          <p className="text-lg font-semibold text-blue-800">
            Total Outstanding Balance to be Settled:
          </p>
          <p className="text-3xl font-bold text-blue-900">
            ${totalOutstanding.toFixed(2)}
          </p>
        </div>
      )}

      <div className="bg-white p-4 rounded-lg shadow mb-6 border">
        <h3 className="text-2xl font-semibold mb-2">Group Budget Overview</h3>
        <div className="flex items-center gap-4">
          {!isEditingBudget ? (
            <div className="flex items-center gap-4">
                <p className="text-gray-700">
                    Total Spent: <span className="font-bold">${totalSpent.toFixed(2)}</span>
                    {group.budget > 0 && (
                        <> of <span className="font-bold">${group.budget.toFixed(2)}</span></>
                    )}
                </p>
                <button onClick={() => setIsEditingBudget(true)} className="text-sm text-blue-600 hover:underline">Edit Budget</button>
            </div>
          ) : (
            <div className="flex items-center gap-2">
              <input 
                type="number"
                value={newBudget}
                onChange={(e) => setNewBudget(e.target.value)}
                className="p-2 border rounded-md w-32"
                placeholder="Set budget"
              />
              <button onClick={handleUpdateBudget} className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600">Save</button>
              <button onClick={() => setIsEditingBudget(false)} className="bg-gray-200 text-gray-800 px-4 py-2 rounded-md hover:bg-gray-300">Cancel</button>
            </div>
          )}
        </div>
      </div>
      
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        <div className="md:col-span-2">
          {/* --- This entire section is now toggleable --- */}
          {!showExpenseSection ? (
            <button
              onClick={() => setShowExpenseSection(true)}
              className="w-full bg-gray-100 text-gray-800 font-bold py-3 px-4 rounded-lg shadow-sm border hover:bg-gray-200 transition-colors"
            >
              Manage Expenses
            </button>
          ) : (
            <div>
              <div className="mb-8">
                {showAddExpense ? (
                  <AddExpenseForm
                    groupId={groupId}
                    members={group.members}
                    onExpenseAdded={() => {
                      handleDataRefresh();
                      setShowAddExpense(false);
                    }}
                    onCancel={() => setShowAddExpense(false)}
                  />
                ) : (
                  <button
                    onClick={() => setShowAddExpense(true)}
                    className="w-full bg-blue-600 text-white font-bold py-3 px-4 rounded-lg shadow hover:bg-blue-700 transition-colors"
                  >
                    Add New Expense
                  </button>
                )}
              </div>
              <h3 className="text-2xl font-semibold mb-4">Expense History</h3>
              <ExpenseList expenses={expenses} />
              <button 
                onClick={() => setShowExpenseSection(false)} 
                className="mt-4 text-sm text-gray-500 hover:underline"
              >
                Hide Expense Section
              </button>
            </div>
          )}
        </div>

        <div className="space-y-6">
          <div className="bg-white p-6 rounded-lg shadow-sm border">
            <h3 className="text-2xl font-semibold mb-4">Members</h3>
            <ul className="space-y-2 mb-6">
              {group.members?.map((member) => (
                <li key={member._id} className="p-3 bg-gray-100 rounded-md">
                  {member.name}
                </li>
              ))}
            </ul>
            <AddMemberForm groupId={groupId} onMemberAdded={handleDataRefresh} />
          </div>
          
          <GroupBalances
            groupId={groupId}
            balances={balances}
            isLoading={loading}
            onSettlementSuccess={handleDataRefresh}
          />
        </div>
      </div>
    </div>
  );
};

export default GroupDetail;
