import React, { useState, useEffect, useContext, useCallback } from 'react';
import CreateGroupForm from '../components/CreateGroupForm';
import GroupList from '../components/GroupList';
import { getMyGroups } from '../api/groupService';
import { AuthContext } from '../context/AuthContext';

const Dashboard = () => {
  // --- State Management ---
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [refreshTrigger, setRefreshTrigger] = useState(0); // A state to trigger data refetching
  
  // --- Context ---
  const { user } = useContext(AuthContext);

  // --- Data Fetching ---
  // The data fetching logic is now self-contained within this useEffect hook.
  useEffect(() => {
    // This inner function can be defined here because it's only used by this effect.
    const fetchGroups = async () => {
      if (!user?.token) {
        setLoading(false);
        return;
      }

      setLoading(true);
      try {
        setError('');
        const userGroups = await getMyGroups(user.token);
        setGroups(userGroups);
      } catch (err) {
        setError('Failed to fetch your groups.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchGroups();
    // This effect now correctly depends on the user object and our manual trigger.
  }, [user, refreshTrigger]);

  // --- Event Handler ---
  // This function is now simpler. Its only job is to trigger the useEffect to run again.
  const handleGroupCreated = () => {
    setRefreshTrigger(count => count + 1);
  };

  // --- Render Logic ---
  if (loading && groups.length === 0) {
    return (
        <div className="flex justify-center items-center h-screen">
            <p className="text-lg text-gray-500">Loading your dashboard...</p>
        </div>
    );
  }

  return (
    // Added a subtle background color to the page wrapper for contrast
    <div className="bg-green-50 min-h-screen">
        <main className="p-8 max-w-4xl mx-auto">
            {/* --- Header Section --- */}
            <div className="pb-8 border-b border-gray-200">
                <h1 className="text-4xl font-extrabold text-gray-900 tracking-tight">
                    Dashboard
                </h1>
                <p className="mt-4 text-lg text-gray-600">
                    Welcome back, {user?.name}! Create a new group or view your existing ones.
                </p>
            </div>

            {/* --- Main Content Section --- */}
            <div className="mt-8">
                <CreateGroupForm onGroupCreated={handleGroupCreated} />

                <div className="mt-12">
                    <h2 className="text-2xl font-bold text-gray-800">Your Groups</h2>
                    
                    {/* --- Styled Error Message --- */}
                    {error && (
                        <div className="mt-4 p-4 bg-red-50 text-red-800 border border-red-200 rounded-lg">
                            <p><span className="font-bold">Oops!</span> {error}</p>
                        </div>
                    )}

                    {/* --- Group List Display --- */}
                    {!loading && !error && (
                        <div className="mt-4">
                            <GroupList groups={groups} />
                        </div>
                    )}
                </div>
            </div>
        </main>
    </div>
  );
};

export default Dashboard;
