// src/pages/Dashboard.js
// This is the updated Dashboard page that fetches and manages the groups state.
import React, { useState, useEffect, useContext } from "react";
import CreateGroupForm from "../components/CreateGroupForm";
import GroupList from "../components/GroupList";
import { getMyGroups } from "../api/groupService";
import { AuthContext } from "../context/AuthContext";

const Dashboard = () => {
  const [groups, setGroups] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const { user } = useContext(AuthContext);

const fetchGroups = useCallback(async () => {
  if (user && user.token) {
    try {
      setLoading(true);
      const userGroups = await getMyGroups(user.token);
      setGroups(userGroups);
    } catch (err) {
      setError("Failed to fetch groups.");
    } finally {
      setLoading(false);
    }
  } else {
    setLoading(false);
  }
}, [user]);

useEffect(() => {
  fetchGroups();
}, [fetchGroups]);

  // Fetch the user's groups when the component loads
useEffect(() => {
  const fetchGroups = async () => {
    if (user && user.token) {
      try {
        setLoading(true);
        const userGroups = await getMyGroups(user.token);
        setGroups(userGroups);
      } catch (err) {
        setError("Failed to fetch groups.");
      } finally {
        setLoading(false);
      }
    } else {
      setLoading(false);
    }
  };

  fetchGroups();
}, [user]);

  // This function is called by the form when a group is created successfully
  const handleGroupCreated = (newGroup) => {
    fetchGroups();

    // Add the new group to the top of the list for an immediate UI update
    setGroups([newGroup, ...groups]);
  };
  if (loading) {
    return (
      <div className="flex justify-center items-center h-screen">
        <p className="text-lg text-gray-500">Loading your dashboard...</p>
      </div>
    );
  }
  return (
    // Added a subtle background color to the page wrapper for contrast
    <div className="bg-gray-50 min-h-screen">
      <main className="p-8 max-w-4xl mx-auto">
        {/* --- Header Section --- */}
        <div className="pb-8 border-b border-gray-200">
          <h1 className="text-4xl font-extrabold text-gray-900 tracking-tight">
            Dashboard
          </h1>
          <p className="mt-4 text-lg text-gray-600">
            Welcome back, {user?.name}! Create a new group or view your existing
            ones.
          </p>
        </div>

        {/* --- Main Content Section --- */}
        <div className="mt-8">
          <CreateGroupForm onGroupCreated={handleGroupCreated} />

          <div className="mt-12">
            <h2 className="text-2xl font-bold text-gray-800">Your Groups</h2>

            {/* --- Styled Error Message --- */}
            {error && (
              <div className="mt-4 p-4 bg-red-50 text-red-800 border border-red-200 rounded-lg">
                <p>
                  <span className="font-bold">Oops!</span> {error}
                </p>
              </div>
            )}

            {/* --- Group List Display --- */}
            {!error && (
              <div className="mt-4">
                <GroupList groups={groups} />
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
