import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { addExpense } from "../api/expenseService";

const AddExpenseForm = ({
  groupId,
  members,
  onExpenseAdded,
  categories = [],
}) => {
  const { user } = useContext(AuthContext);

  const [description, setDescription] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [splitType, setSplitType] = useState("equal");
  const [customSplits, setCustomSplits] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    if (members) {
      setCustomSplits(
        members.map((m) => ({ user: m._id, name: m.name, amount: "" }))
      );
    }
  }, [members]);

  const handleCustomSplitChange = (userId, value) => {
    const newSplits = customSplits.map((split) =>
      split.user === userId ? { ...split, amount: value } : split
    );
    setCustomSplits(newSplits);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    const totalAmount = parseFloat(amount);
    if (!description || !totalAmount || totalAmount <= 0) {
      setError("Please enter a valid description and amount.");
      return;
    }
    
    // --- THE FIX IS HERE ---
    // The expenseData object now includes the splitType from the start.
    let expenseData = {
      description,
      amount: totalAmount,
      group: groupId,
      category: category || "None",
      splitType: splitType, // Explicitly include the split type
    };
    // ----------------------

    // The 'paidBy' field is handled by the backend, so we don't need to send it.

    if (splitType === "custom") {
      const splitsForApi = customSplits
        .filter((s) => parseFloat(s.amount) > 0)
        .map((s) => ({ user: s.user, amount: parseFloat(s.amount) }));

      const sumOfCustomSplits = splitsForApi.reduce(
        (sum, s) => sum + s.amount,
        0
      );

      if (Math.abs(sumOfCustomSplits - totalAmount) > 0.01) {
        setError(
          `Custom splits must add up to $${totalAmount.toFixed(
            2
          )}. Current total: $${sumOfCustomSplits.toFixed(2)}.`
        );
        return;
      }
      expenseData.splits = splitsForApi;
    }
    // Note: For 'equal' split, we don't need to send the splits array.
    // The backend will calculate it based on the group members.

    try {
      await addExpense(expenseData, user.token);
      onExpenseAdded();

      // Reset form
      setDescription("");
      setAmount("");
      setCategory("");
      setSplitType("equal");
      if (members) {
        setCustomSplits(
          members.map((m) => ({ user: m._id, name: m.name, amount: "" }))
        );
      }
    } catch (err) {
      setError("Failed to add expense. Please try again.");
    }
  };

  const totalCustomAmount = customSplits.reduce(
    (sum, s) => sum + (parseFloat(s.amount) || 0),
    0
  );
  const remainingAmount = (parseFloat(amount) || 0) - totalCustomAmount;

  return (
    <div className="p-6 bg-white rounded-lg shadow-md border">
      <h3 className="text-xl font-semibold mb-4">Add a New Expense</h3>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700">
            Description
          </label>
          <input
            type="text"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className="w-full mt-1 p-2 border rounded"
            placeholder="e.g. Internet Bill"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Amount ($)
          </label>
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            className="w-full mt-1 p-2 border rounded"
            placeholder="0.00"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Category
          </label>
          <select
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            className="w-full mt-1 p-2 border rounded bg-white"
            disabled={categories.length === 0}
          >
            <option value="">
              {categories.length > 0
                ? "Select a category"
                : "No categories available"}
            </option>
            {categories.map((cat) => (
              <option key={cat} value={cat}>
                {cat}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700">
            Split Type
          </label>
          <select
            value={splitType}
            onChange={(e) => setSplitType(e.target.value)}
            className="w-full mt-1 p-2 border rounded"
          >
            <option value="equal">Split Equally</option>
            <option value="custom">Custom Split</option>
          </select>
        </div>

        {splitType === "custom" && (
          <div>
            <h4 className="text-md font-medium text-gray-700 mb-2">
              Custom Split Amounts
            </h4>
            <p
              className={`text-sm mb-3 ${
                remainingAmount === 0 ? "text-green-600" : "text-red-600"
              }`}
            >
              Amount remaining: ${remainingAmount.toFixed(2)}
            </p>
            {customSplits.map((split) => (
              <div key={split.user} className="flex items-center gap-4 mb-2">
                <label className="w-1/3">{split.name}</label>
                <input
                  type="number"
                  value={split.amount}
                  onChange={(e) =>
                    handleCustomSplitChange(split.user, e.target.value)
                  }
                  className="flex-grow p-2 border rounded"
                  placeholder="0.00"
                />
              </div>
            ))}
          </div>
        )}

        {error && <p className="text-sm text-red-600">{error}</p>}

        <button
          type="submit"
          className="w-full bg-green-600 text-white font-bold py-2 px-4 rounded hover:bg-green-700"
        >
          Add Expense
        </button>
      </form>
    </div>
  );
};

export default AddExpenseForm;
