import React from "react";

const ActivityFeed = ({ items }) => {
  if (!items || items.length === 0) {
    return <p className="mt-4 text-gray-500">No activity in this group yet.</p>;
  }

  return (
    // Add max-h-[value] and overflow-y-auto to the container
    <div className="mt-4 space-y-3 max-h-[500px] overflow-y-auto pr-2">
      {items.map((item) => {
        // Conditionally render based on the item type from the backend
        if (item.type === "expense") {
          return (
            <div
              key={`expense-${item._id}`} // Use a unique key prefix
              className="p-4 bg-white border rounded-lg shadow-sm"
            >
              <div className="flex items-center justify-between">
                <p className="font-semibold text-gray-800">
                  {item.description}
                </p>
                <p className="font-bold text-lg text-red-600">
                  -${item.amount.toFixed(2)}
                </p>
              </div>
              <p className="mt-1 text-sm text-gray-600">
                {item.paidBy ? item.paidBy.name : "A user"} paid on{" "}
                {new Date(item.createdAt).toLocaleString()}
              </p>
            </div>
          );
        }
        if (item.type === "settlement") {
          return (
            <div
              key={`settlement-${item._id}`} // Use a unique key prefix
              className="p-4 bg-green-50 border-l-4 border-green-400"
            >
              <div className="flex items-center justify-between">
                <p className="font-semibold text-gray-800">Settlement</p>
                <p className="font-bold text-lg text-green-600">
                  +${item.amount.toFixed(2)}
                </p>
              </div>
              <p className="mt-1 text-sm text-gray-600">
                {item.from ? item.from.name : "A user"} paid{" "}
                {item.to ? item.to.name : "another user"} on{" "}
                {new Date(item.createdAt).toLocaleDateString()}
              </p>
            </div>
          );
        }
        return null;
      })}
    </div>
  );
};

export default ActivityFeed;
