// src/components/GroupBalances.js
import React, { useState, useEffect, useContext, useCallback } from "react";
import { AuthContext } from "../context/AuthContext";
import { getGroupBalances, recordSettlement } from "../api/settlementService";

const GroupBalances = ({ groupId, onDataRefresh }) => {
  const [balances, setBalances] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const { user: currentUser } = useContext(AuthContext);

  const [toUserId, setToUserId] = useState("");
  const [fromUserId, setFromUserId] = useState(""); // State for the payer dropdown
  const [settleAmount, setSettleAmount] = useState("");

  const fetchBalances = useCallback(async () => {
    if (!currentUser?.token) return;
    setIsLoading(true);
    try {
      const balanceData = await getGroupBalances(groupId, currentUser.token);
      setBalances(balanceData);
    } catch (err) {
      setError("Failed to load group balances.");
    } finally {
      setIsLoading(false);
    }
  }, [groupId, currentUser]);

  useEffect(() => {
    fetchBalances();
  }, [fetchBalances]);

  const handleSettleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    const amount = parseFloat(settleAmount);

    if (!fromUserId || !toUserId || !amount || amount <= 0) {
      setError("Please select a payer, recipient, and enter a valid amount.");
      return;
    }

    const settlementData = {
      from: fromUserId,
      to: toUserId,
      group: groupId,
      amount: amount,
    };

    try {
      await recordSettlement(settlementData, currentUser.token);

      // Reset form and refresh data
      setToUserId("");
      setFromUserId("");
      setSettleAmount("");
      onDataRefresh();
      fetchBalances();
    } catch (err) {
      setError("Failed to record settlement.");
    }
  };

  if (isLoading) {
    return (
      <p className="text-center mt-4 text-gray-500">Loading balances...</p>
    );
  }

  // Correctly filter the balances array
  const debtors = balances.filter((b) => b.net < 0);
  const creditors = balances.filter((b) => b.net > 0);

  return (
    <div className="p-6 bg-white rounded-lg shadow-sm border space-y-6">
      <h3 className="text-2xl font-bold">Group Balances</h3>

      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        <div>
          <h4 className="font-semibold text-lg text-red-600 mb-2">Who Owes</h4>
          {debtors.length > 0 ? (
            debtors.map((user) => (
              <div
                key={user.userId}
                className="flex justify-between p-2 bg-red-50 rounded-md mb-2 text-sm"
              >
                <span>{user.name}</span>
                <span className="font-bold">
                  Owes ${Math.abs(user.net).toFixed(2)}
                </span>
              </div>
            ))
          ) : (
            <p className="text-gray-500 text-sm">Everyone is settled up.</p>
          )}
        </div>
        <div>
          <h4 className="font-semibold text-lg text-green-600 mb-2">
            Who is Owed
          </h4>
          {creditors.length > 0 ? (
            creditors.map((user) => (
              <div
                key={user.userId}
                className="flex justify-between p-2 bg-green-50 rounded-md mb-2 text-sm"
              >
                <span>{user.name}</span>
                <span className="font-bold">
                  Is owed ${user.net.toFixed(2)}
                </span>
              </div>
            ))
          ) : (
            <p className="text-gray-500 text-sm">No one is owed money.</p>
          )}
        </div>
      </div>

      <div className="border-t pt-6">
        <h4 className="font-semibold text-xl mb-3">Record a Payment</h4>
        <form onSubmit={handleSettleSubmit} className="space-y-4">
          <div>
            <label
              htmlFor="from"
              className="block text-sm font-medium text-gray-700"
            >
              From (Payer)
            </label>
            <select
              id="from"
              name="from"
              value={fromUserId}
              onChange={(e) => setFromUserId(e.target.value)}
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
            >
              <option value="">Select a payer...</option>
              {/* THE FIX: Map over the debtors array correctly, using user.userId */}
              {debtors.map((user) => (
                <option key={user.userId} value={user.userId}>
                  {user.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label
              htmlFor="to"
              className="block text-sm font-medium text-gray-700"
            >
              To (Recipient)
            </label>
            <select
              id="to"
              name="to"
              value={toUserId}
              onChange={(e) => setToUserId(e.target.value)}
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
            >
              <option value="">Select a recipient...</option>
              {/* THE FIX: Map over the creditors array correctly, using user.userId */}
              {creditors.map((user) => (
                <option key={user.userId} value={user.userId}>
                  {user.name}
                </option>
              ))}
            </select>
          </div>
          <div>
            <label
              htmlFor="amount"
              className="block text-sm font-medium text-gray-700"
            >
              Amount
            </label>
            <input
              type="number"
              id="amount"
              name="amount"
              value={settleAmount}
              onChange={(e) => setSettleAmount(e.target.value)}
              placeholder="0.00"
              className="mt-1 block w-full p-2 border rounded-md shadow-sm"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-purple-600 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded-md"
          >
            Settle Up
          </button>
          {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default GroupBalances;
