import React from "react";

// Define the formatting options as a constant here
const dateTimeFormatOptions = {
  year: "numeric",
  month: "short",
  day: "numeric",
  hour: "2-digit",
  minute: "2-digit",
  hour12: true,
};

const ActivityFeed = ({ items }) => {
  if (!items || items.length === 0) {
    return <p className="mt-4 text-gray-500">No activity in this group yet.</p>;
  }

  return (
    <div className="mt-4 space-y-3 max-h-[500px] overflow-y-auto pr-2">
      {items.map((item) => {
        // Now you can reuse the constant for formatting
        const formattedDate = new Date(item.createdAt).toLocaleString(
          undefined,
          dateTimeFormatOptions
        );

        if (item.type === "expense") {
          return (
            <div key={`expense-${item._id}`} className="...">
              {/* ... */}
              <p className="mt-1 text-sm text-gray-600">
                {item.paidBy ? item.paidBy.name : "A user"} paid on{" "}
                {formattedDate}
              </p>
            </div>
          );
        }
        if (item.type === "settlement") {
          return (
            <div key={`settlement-${item._id}`} className="...">
              {/* ... */}
              <p className="mt-1 text-sm text-gray-600">
                {item.from ? item.from.name : "A user"} paid{" "}
                {item.to ? item.to.name : "another user"} on{" "}
                {formattedDate}
              </p>
            </div>
          );
        }
        return null;
      })}
    </div>
  );
};

export default ActivityFeed;
