import React, { useState, useEffect, useContext, useCallback } from "react";
// Corrected the function name from settlePayment to recordSettlement
import { getGroupBalances, recordSettlement } from "../api/settlementService";
import { AuthContext } from "../context/AuthContext";

const GroupBalances = ({ groupId }) => {
  const [balances, setBalances] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState("");
  const [settlement, setSettlement] = useState({
    from: "",
    to: "",
    amount: "",
  });
  const { user: currentUser } = useContext(AuthContext);
  console.log("Received groupId:", groupId);
  console.log("Current user from context:", currentUser);
  const fetchBalances = useCallback(async () => {
    if (!groupId || !currentUser.token) return;

    setIsLoading(true);
    try {
      const data = await getGroupBalances(groupId, currentUser.token);
      setBalances(data);
      setError("");
    } catch (err) {
      setError(err.message || "Failed to fetch balances.");
    } finally {
      setIsLoading(false);
    }
  }, [groupId, currentUser.token]);

  useEffect(() => {
    if (groupId && currentUser.token) {
      fetchBalances();
    } else {
      setIsLoading(false);
    }
  }, [fetchBalances, groupId, currentUser.token]);

  const handleSettleSubmit = async (e) => {
    e.preventDefault();
    if (!settlement.from || !settlement.to || !settlement.amount) {
      setError("Please fill out all settlement fields.");
      return;
    }
    try {
      await recordSettlement(
        { ...settlement, group: groupId },
        currentUser.token
      );
      setSettlement({ from: "", to: "", amount: "" }); // Reset form
      fetchBalances(); // Refresh balances after settlement
      setError(""); // Clear any previous errors
    } catch (err) {
      setError(err.message || "Failed to record settlement.");
    }
  };

  const handleSettlementChange = (e) => {
    const { name, value } = e.target;
    setSettlement((prev) => ({ ...prev, [name]: value }));
  };

  if (isLoading) {
    return <p className="text-center mt-4">Loading balances...</p>;
  }

  if (error) {
    return <p className="text-center mt-4 text-red-500">{error}</p>;
  }

  if (!balances) {
    return null;
  }

  const members = Object.entries(balances);
  const debtors = members.filter(([_, data]) => data.net < 0);
  const creditors = members.filter(([_, data]) => data.net > 0);
  // New logic: Check if any money has been paid at all to differentiate between a new group and a settled group.
  const totalPaidInGroup = members.reduce(
    (sum, [_, data]) => sum + data.paid,
    0
  );

  return (
    <div className="mt-8 p-6 bg-white rounded-lg shadow-md">
      <h3 className="text-2xl font-bold mb-4">Group Balances</h3>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <div>
          <h4 className="font-semibold text-lg text-red-600 mb-2">
            Who Owes Money
          </h4>
          {debtors.length > 0 ? (
            debtors.map(([userId, data]) => (
              <div
                key={userId}
                className="flex justify-between items-center p-2 bg-red-50 rounded-md mb-2"
              >
                <span>{data.name}</span>
                <span className="font-bold text-red-700">
                  Owes ${Math.abs(data.net).toFixed(2)}
                </span>
              </div>
            ))
          ) : (
            <p className="text-gray-500">No one owes money.</p>
          )}
        </div>
        <div>
          <h4 className="font-semibold text-lg text-green-600 mb-2">
            Who Is Owed Money
          </h4>
          {creditors.length > 0 ? (
            creditors.map(([userId, data]) => (
              <div
                key={userId}
                className="flex justify-between items-center p-2 bg-green-50 rounded-md mb-2"
              >
                <span>{data.name}</span>
                <span className="font-bold text-green-700">
                  Is owed ${data.net.toFixed(2)}
                </span>
              </div>
            ))
          ) : (
            <p className="text-gray-500">No one is owed money.</p>
          )}
        </div>
      </div>

      {/* Settle Up Section */}
      <div className="mt-6 border-t pt-6">
        <h4 className="font-semibold text-xl mb-3">Record a Payment</h4>
        {/* THE FIX: Differentiate between a new group and a settled group. */}
        {totalPaidInGroup === 0 ? (
          <div className="text-center p-4 bg-gray-50 rounded-md">
            <p className="text-gray-600">
              No expenses have been added to this group yet.
            </p>
          </div>
        ) : debtors.length === 0 && creditors.length === 0 ? (
          <div className="text-center p-4 bg-gray-50 rounded-md">
            <p className="text-gray-600">
              Everything is settled up. There are no outstanding balances to
              pay.
            </p>
          </div>
        ) : (
          <form onSubmit={handleSettleSubmit} className="space-y-4">
            <div className="flex flex-wrap gap-4 items-end">
              <div className="flex-1 min-w-[150px]">
                <label
                  htmlFor="from"
                  className="block text-sm font-medium text-gray-700"
                >
                  From
                </label>
                <select
                  name="from"
                  value={settlement.from}
                  onChange={handleSettlementChange}
                  className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
                >
                  <option value="">Select Payer</option>
                  {debtors.map(([userId, data]) => (
                    <option key={userId} value={userId}>
                      {data.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="flex-1 min-w-[150px]">
                <label
                  htmlFor="to"
                  className="block text-sm font-medium text-gray-700"
                >
                  To
                </label>
                <select
                  name="to"
                  value={settlement.to}
                  onChange={handleSettlementChange}
                  className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
                >
                  <option value="">Select Recipient</option>
                  {creditors.map(([userId, data]) => (
                    <option key={userId} value={userId}>
                      {data.name}
                    </option>
                  ))}
                </select>
              </div>
              <div className="flex-1 min-w-[100px]">
                <label
                  htmlFor="amount"
                  className="block text-sm font-medium text-gray-700"
                >
                  Amount
                </label>
                <input
                  type="number"
                  name="amount"
                  placeholder="0.00"
                  step="0.01"
                  value={settlement.amount}
                  onChange={handleSettlementChange}
                  className="mt-1 block w-full p-2 border border-gray-300 rounded-md shadow-sm"
                />
              </div>
              <button
                type="submit"
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-md"
              >
                Settle Up
              </button>
            </div>
          </form>
        )}
      </div>

                  <div className="mt-6 border-t pt-6">
                <h4 className="font-semibold text-lg mb-2">Payment History</h4>
                {settlementHistory.length > 0 ? (
                    <ul className="space-y-2">
                        {settlementHistory.map(s => (
                            <li key={s._id} className="p-3 bg-gray-50 border rounded-md text-sm">
                                <p className="text-gray-800">
                                    <span className="font-bold text-blue-600">{s.from?.name || 'A user'}</span>
                                    {' '}paid{' '}
                                    <span className="font-bold text-green-600">{s.to?.name || 'another user'}</span>
                                    {' '}<strong>${s.amount.toFixed(2)}</strong>
                                </p>
                                <p className="text-xs text-gray-500 mt-1">
                                    {new Date(s.date).toLocaleDateString()}
                                </p>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p className="text-sm text-gray-500">No settlements have been recorded yet.</p>
                )}
            </div>
    </div>
  );
};

export default GroupBalances;
