import React, { useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { recordSettlement } from "../api/settlementService";

const GroupBalances = ({ groupId, members, onDataRefresh }) => {
  const { user: currentUser } = useContext(AuthContext);
  const [settlement, setSettlement] = useState({ to: "", amount: "" });
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  const fromUser = currentUser.userId;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSettlement((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setMessage("");

    const { to, amount } = settlement;
    if (!fromUser || !to || !amount || parseFloat(amount) <= 0) {
      setError("Please select a recipient and enter a valid amount.");
      return;
    }

    try {
      await recordSettlement(
        {
          from: fromUser,
          to: to,
          group: groupId,
          amount: parseFloat(amount),
        },
        currentUser.token
      );

      setMessage("Settlement recorded successfully!");
      onDataRefresh();
      setSettlement({ to: "", amount: "" });
    } catch (err) {
      setError("Failed to record settlement.");
    }
  };

  return (
    <div className="mt-6 p-6 border rounded-lg shadow-sm bg-white">
      <h3 className="text-xl font-semibold mb-4">Record a Payment</h3>
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-700">
            From
          </label>
          <input
            type="text"
            value={currentUser.name}
            className="w-full p-2 border rounded mt-1 bg-gray-100"
            disabled
          />
        </div>
        <div className="mb-4">
          <label
            htmlFor="to"
            className="block text-sm font-medium text-gray-700"
          >
            To
          </label>
          <select
            id="to"
            name="to"
            value={settlement.to}
            onChange={handleChange}
            className="w-full p-2 border rounded mt-1"
          >
            <option value="">Select a member...</option>
            {members
              .filter((m) => m._id !== fromUser)
              .map((m) => (
                <option key={m._id} value={m._id}>
                  {m.name}
                </option>
              ))}
          </select>
        </div>
        <div className="mb-4">
          <label
            htmlFor="amount"
            className="block text-sm font-medium text-gray-700"
          >
            Amount ($)
          </label>
          <input
            type="number"
            id="amount"
            name="amount"
            value={settlement.amount}
            onChange={handleChange}
            placeholder="0.00"
            className="w-full p-2 border rounded mt-1"
          />
        </div>
        <button
          type="submit"
          className="w-full mt-2 bg-purple-500 hover:bg-purple-700 text-white font-bold py-2 px-4 rounded"
        >
          Settle Up
        </button>
        {error && <p className="text-red-600 mt-2">{error}</p>}
        {message && <p className="text-green-600 mt-2">{message}</p>}
      </form>
    </div>
  );
};

export default GroupBalances;
